/* Name of general package we are working with tutorial */
package datatypes;

/* Main class that can receive other other methods
 * instantiated from classes you previous declared*/
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Hello World*/
		/* System.out.println("Hello World"); */
		
		/* primitive datatypes, not changeable */
		int hello_world = 5;
		int number = 10;
		double num2 = 5.0;
		boolean b = false;
		char c = '9';
		
		/* Not primitive datatype */
		String string_var = "string_datatype";
		
		/* Pointing vars */
		int tim = hello_world;
		
		
		System.out.println(hello_world);
		System.out.println(num2);
		System.out.println(number);
		System.out.println(b);
		System.out.println(c);
		System.out.println(string_var);
		System.out.println(tim);
	}
}
 